from django.db import connectionfrom datetime import date,datetimedef status_code_500(argv):    data = {}    data['status'] = 500    data['message'] = "Server Error"    data['data'] = argv    return datadef status_code_200(argv):    data = {}    data['status'] = 200    data['message'] = "Secuess..."    data['data'] = argv    return datadef status_code_check(argv):    if  400 <= argv <= 499:        return "You Have 4XX error..."    if  500 <= argv <= 500:        return "You Have 5XX error..."def __default(obj):    if isinstance(obj, str):        return obj    elif isinstance(obj, datetime):        return obj.strftime('%Y-%m-%dT%H:%M:%S')    elif isinstance(obj, date):        return obj.strftime('%Y-%m-%d')    else:        raise TypeError('%r is not JSON serializable' % obj)def format_data(obj):    return __default(obj)def sqlquery_dict(sql):    cursor = connection.cursor()    cursor.execute(sql,None)    "将游标返回的结果保存到一个字典对象中"    desc = cursor.description    data = [        dict(zip([col[0] for col in desc],row))        for row in cursor.fetchall()    ]    return data